import React from 'react';
import './css/CollectionsPage.css';
import IndexedDbRepository from './IndexedRepository';
import {Link, withRouter} from 'react-router-dom';

class CollectionsPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            collections: []
        }

        this.handleDelete = async( idToDelete ) => {
            await this.repository.deleteById( 'collections', idToDelete );
            this.setState( { images: await this.repository.findAll('collections') } );
          };
    }

    componentDidMount() {
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll('collectionss').then( collections => this.setState( { collections } ) );
    }

    render() {
        return (
            <div class="container">
                <div id="photo-wall">
                    <ul>
                    {this.state.collections.map((collectiondata) => {
                    return (
                        <li>
                        <div class="post">
                            <h1>{collectiondata.name}</h1>
                        </div>
                        </li>
                    )
                    })}   
                    </ul>
                </div>
                <button type = "button">Добавить коллекцию</button>
            </div>
        );
    }
}

export default CollectionsPage;
