import React from 'react';


class StorageInfo extends React.Component {
    constructor(props) {
        super(props);

        this.clearStorage = this.clearStorage.bind(this);
        this.state = {
            recordsNum: 0
        }
    }

    componentDidMount() {
        this.repository = new IndexedDbRepository();
        this.repository.count('photos').then(recordsNum => this.setState({recordsNum}));
    }

    clearStorage = async() => {
        await this.repository.deleteAll('photos');
        this.setState( { images: await this.repository.findAll('photos') } );
    }

  render()
  {
    return (
      <div>

        <button type = "button" onClick = {async () => {await this.clearStorage()}}>Очистить хранилище</button>
      </div>
    );
  }
}

export default StorageInfo;
