import React from 'react';
import './css/CollectionsPage.css';
import IndexedDbRepository from './IndexedRepository';
import {Link, withRouter} from 'react-router-dom';

class CollectionsPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            collections: []
        }

        this.handleDelete = async( idToDelete ) => {
            var colImg = await this.repository.searchByField('photos', 'collections', idToDelete);
            if(window.confirm('Удалить также и изображения коллекции?'))
            {
                for (const element of colImg) {
                    await this.repository.deleteById( 'photos', element.id );
                }
            }
            else
            {
                for (const element of colImg) {
                    element.collection = 'Другое';
                    await this.repository.save( 'photos', element);
                }
            }
            await this.repository.deleteById( 'collections', idToDelete );
            this.setState( { images: await this.repository.findAll('collections') } );
        };
    }

    componentDidMount() {
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll('collections').then( collections => this.setState( { collections } ) );
    }

    render() {
        return (
            <div class="container">
                <div id="photo-wall">
                    <ul>
                    {this.state.collections.map((collectiondata) => {
                    return (
                        <li>
                        <div class="post">
                        <img width = "200px" height = "100px" src = {'data:image/jpeg;base64,' + btoa(collectiondata.colPreview)}/>
                        <Link to={/collectionpage/+ collectiondata.colName} class="btnlink" >{collectiondata.colName}</Link>
                        <button type = "button" onClick = {async () => {await this.handleDelete(collectiondata.colName)}}>Удалить</button>
                        </div>
                        </li>
                    )
                    })}   
                    </ul>
                </div>
                <button type = "button"><Link to='/addcollection' class="btnlink" >Добавить коллекцию</Link></button>
            </div>
        );
    }
}

export default CollectionsPage;
