import React from 'react';
import './css/CollectionsPage.css';
import IndexedDbRepository from './IndexedRepository';
import {Link, withRouter} from 'react-router-dom';

class AllImgPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            images: []
        }

        this.search = this.search.bind(this);
        this.saveBase64AsFile = this.saveBase64AsFile.bind(this);
        this.handleDelete = async( idToDelete ) => {
            await this.repository.deleteById( 'photos', idToDelete );
            this.setState( { images: await this.repository.findAll('photos') } );
          };
    }

    componentDidMount() {
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll('photos').then( images => this.setState( { images } ) );
        
    }

    search(e) {
        if(e.target.value === '') {
            this.repository.findAll('photos').then( images => this.setState( { images } ) );
        }
        else {
            this.repository.searchByField('photos', 'name', e.target.value).then(images => this.setState( { images } ) );
        }

    }

    saveBase64AsFile(base64, fileName) {
        var link = document.createElement("a");
    
        document.body.appendChild(link);
    
        link.setAttribute("href", base64);
        link.setAttribute("download", fileName);
        link.click();
    }

    render() {
        return (
            <div class="container">
                <div id="photo-wall">
                    <input id = "search" type = "text" placeholder = "Введите название картинки..." onChange = {this.search}/>
                    <button type = "button">Поиск</button>
                    <ul>
                        {this.state.images.map((imgcontent) => {
                    return (
                        <li>
                        <div class="post">
                            <img width = "200px" height = "100px" src = {'data:image/' + imgcontent.mimetype.split('.').pop() + ';base64,' + btoa(imgcontent.image)}/>
                            <h2 class="title">{imgcontent.name}</h2>
                            <p class="description">Описание: {imgcontent.description}</p>
                            <p class="description">Формат: {imgcontent.format}</p>
                            <p class="description">Дата: {imgcontent.date}</p>
                            <p class="description">Тип файла: {imgcontent.mimetype}</p>
                            <p class="description">Вес: {imgcontent.weight}</p>
                            <p class="description">Размеры: {imgcontent.size}</p>
                            <p class="description">Коллекция: {imgcontent.collection}</p>
                            <button type = "button" onClick = {async () => {await this.handleDelete(imgcontent.id)}}>Удалить</button>
                            <button class="btn green" type="button"><Link to={/edit/+ imgcontent.id} class="btnlink" >Редактировать</Link></button>
                            <button type="button" onClick = {this.saveBase64AsFile(imgcontent.image)}>Сохранить картинку</button>
                        </div>
                        </li>
                    )
                    })}     
                    </ul>
                </div>
                <button type = "button"><Link to='/add' class="btnlink" >Добавить</Link></button>
                <button type = "button"><Link to='/collections' class="btnlink" >Коллекции</Link></button>
            </div>
        );
    }
}

export default withRouter(AllImgPage);
