import React from 'react';
import './css/common.css';
import './css/Buttons.css';
import IndexedDbRepository from './IndexedRepository';
import Menu from './Menu';

class StorageInfo extends React.Component {
    constructor(props) {
        super(props);

        this.clearStorage = this.clearStorage.bind(this);
        this.state = {
            recordsNum: '',
            total: 0,
            nowUsing: 0,
            usingPercent: 0
        }
    }

    componentDidMount() {
      document.title = "Хранилище";
        this.repository = new IndexedDbRepository();
        this.repository.count('photos').then(recordsNum => this.setState({recordsNum}));

        navigator.storage.estimate().then((result) => {
          this.setState({max: result.quota/1000000000});
          this.setState({current: result.usage/1000000000});
        });
    }

    clearStorage = async() => {
        this.repository.deleteAll('photos').then(alert("Хранилище было ичещено"));
    }

  render()
  {
    return (
      <div class = "container">
        <div class = "header">
          <Menu />
          <h1>Информация о хранилище</h1>
        </div>

        <p>В базе данных {this.state.recordsNum} записей</p>
        <p>Максимальное количество записей: 500</p>
        <p>Доступно ещё {500-parseInt(this.state.recordsNum)} записей</p>
        <button class = "button" type = "button" onClick = {async () => {await this.clearStorage()}}>Очистить хранилище</button>
      </div>
    );
  }
}

export default StorageInfo;
