import React from 'react';
import './css/CollectionsPage.css';
import './css/common.css';
import IndexedDbRepository from './IndexedRepository';
import {Link, withRouter} from 'react-router-dom';

class AddCollectionPg extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            colName: '',
            colPreview: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleImageChange = this.handleImageChange.bind(this);
        this.handleAdd = async() => {

            await this.repository.save( 'collections', { colName: this.state.colName,
                                                colPreview: this.state.colPreview }
                                             );
          };
    }

    componentDidMount() {
        document.title = "Добавление коллекции";
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll('collections').then( collections => this.setState( { collections } ) );
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    handleImageChange(e) {
        var file = e.target.files[0];
        var reader = new FileReader();
        var preview = document.getElementById("colPreview");
        reader.readAsBinaryString(file);
        
				reader.onload = function(e) {
                    let bits = e.target.result;
                    preview.src = 'data:image/jpeg;base64,' + btoa(bits);    
                        this.setState({
                            colPreview: bits
                        });    
                }.bind(this);

    }


    render() {
        return (
            <div class = "container">
            <div class = "header"> 
                <h1>Добавление новой коллекции</h1>
            </div>
            <div class = "imgcontent">
                <img id="colPreview"/><br/>
                <input  type="file" name="colPreview" onChange={this.handleImageChange}/><br/>
                <input class = "input_1" id="colName" class="input_1" name="colName" type="text" value={this.state.name} onChange={this.handleChange} required/><br/>
                <button type="button" class="button" onClick={this.handleAdd}>Добавить</button>
            </div>
            </div>
        );
    }
}

export default AddCollectionPg;
