import React from 'react';
import './css/common.css';
import './css/Buttons.css';
import './css/Inputs.css';
import IndexedDbRepository from './IndexedRepository';

class EditImg extends React.Component {
    constructor(props) {
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.handleImageChange = this.handleImageChange.bind(this);
        this.state = {
            image: null,
            name: null,
            description: null,
            format: null,
            mimetype: null,
            weight: null,
            size: null,
            collection: null,
            date: null,
            id: null,
            colList: []
        }

        this.handleAdd = async() => {

            await this.repository.save( 'photos', this.state
                                             );
            this.setState( { tasks: null } );
            this.setState( { tasks: await this.repository.findAll('photos') } );
            console.log(this.state.tasks);
          };

    }
    componentDidMount() {
        document.title = "Редактирование картинки";
        this.repository = new IndexedDbRepository();
        //this.repository.findAll().then( tasks => this.setState( { tasks } ) );
        this.repository.findById('photos', parseInt(this.props.match.params.id)).then( r => this.setState( {             
            image: r.image,
            name: r.name,
            description: r.description,
            format: r.format,
            mimetype: r.mimetype,
            weight: r.weight,
            size: r.size,
            collection: r.collection,
            date: r.date,
            tags: r.tags,
            id: r.id } ) );
            this.repository.findAll('collections'). then( colList => this.setState( { colList } ) );
        //this.repository.findById(this.props.match.params.id).then(obj => this.setState({obj}));
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    handleImageChange(e) {
        var file = e.target.files[0];
        this.setState({
            mimetype: '.' + file.type.split('/')[1],
            weight: (file.size/1024/1024).toFixed(2) + " МБ"
        });

        console.log(file);
        var reader = new FileReader();
        console.log(reader);
        var preview = document.getElementById("preview");
        reader.readAsBinaryString(file);
        
				reader.onload = function(e) {
                    let bits = e.target.result;
                    preview.src = 'data:image/jpeg;base64,' + btoa(bits);
                    var img = new Image();
                    img.src = 'data:image/jpeg;base64,' + btoa(bits);
                    img.onload = () => {
                        
                    var size = img.height + "X" + img.width + " px";
                        this.setState({
                            image: bits,
                            size: size
                        });
                    }
                    
                }.bind(this);

    }



    render() {
        return (
            <div class = "container">
            <div class = "header"> 
                <h1>Редактирование картинки</h1>

            </div>
            <div class = "imgcontent">
            <img src = {'data:image/jpeg;base64,' + btoa(this.state.image)} id="preview"/><br/>
            <input type="file" name="image" onChange={this.handleImageChange}/><br/>
            <input class = "input_1" id="name" class="input_1" name="name" type="text" value={this.state.name} onChange={this.handleChange} required/><br/>
            <textarea class = "textarea_1" name="description" type="text" value={this.state.description} onChange={this.handleChange}></textarea>
            <label>Формат</label>
            <select class = "select" name="format" value = {this.state.format} onChange={this.handleChange} onLoad={this.handleChange}><br/>
                <option value="Альбом">Альбом</option>
                <option value="Портрет">Портрет</option>
                <option value="Квадрат">Квадрат</option>
            </select><br/>
            <label>Коллекция</label>
            <select class = "select" name="collection" onChange={this.handleChange}>
                <option value="Другое">Другое</option>
                {
                    this.state.colList.map((item) => {
                        return(
                            <option value={item.colName}>{item.colName}</option>
                        );
                    })
                }
            </select><br/>
            <button type="button" class="button" onClick={this.handleAdd}>Изменить</button>
            </div>
            </div>
        );
    }
}


export default EditImg;
