import React from 'react';
import './css/CollectionsPage.css';
import IndexedDBRepository from './IndexedRepository.js';

class AddImg extends React.Component {
    constructor(props) {
        super(props);
        this.add = this.add.bind(this);
        this.state = {
            store: '',
            name: 'rgergev',
            format: 'ergerger',
            description: '', 
            image: ''
        }
    }
    componentDidMount() {
        if ( !window.indexedDB ) window.indexedDB = window.indexedDB || window.mozIndexedDB || window.webkitIndexedDB || window.msIndexedDB;
        if ( !window.IDBTransaction ) window.IDBTransaction = window.webkitIDBTransaction || window.msIDBTransaction;
        if ( !window.IDBKeyRange ) window.IDBKeyRange = window.webkitIDBKeyRange || window.msIDBKeyRange;

        var openRequest = indexedDB.open("test",1);
        var db;
        openRequest.onsuccess = function(e) {
            console.log("Success!");
            db = e.target.result;
        }
 
        openRequest.onerror = function(e) {
            console.log("Error");
            console.dir(e);
        }

        var transaction = db.transaction(["notes"],"readwrite");
        this.setState(
            {store : transaction.objectStore("notes")}
        )
        
    }

    add() {

        var request = this.state.store.add([this.state.name, this.state.format],1);
    }

    render() {
        return (
            <div>
                {this.add()}
            </div>
        );
    }
}


export default AddImg;
