import React from 'react';
import './css/CollectionsPage.css';
import IndexedDbRepository from './IndexedRepository';

class AllImgPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            images: []
        }
    }

    componentDidMount() {
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll().then( images => this.setState( { images } ) );
    }
    render() {
        return (
            <div class="container">
                <div id="photo-wall">
                    <ul>
                        {this.state.images.map((imgcontent) => {
                    return (
                        <li>
                        <div class="post">
                            
                                        <h2 class="title">{imgcontent.name}</h2>
                                        <h3 class="description">{imgcontent.description}</h3>
                        </div>
                        </li>
                    )
                    })}     
                    </ul>
                </div>
            </div>
        );
    }
}

export default AllImgPage;
