import React from 'react';
import './css/CollectionsPage.css';
import IndexedDbRepository from './IndexedRepository';

class AddImg extends React.Component {
    constructor(props) {
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.handleImageChange = this.handleImageChange.bind(this);
        this.state = {
            name: '',
            format: 'Альбом',
            description: '', 
            size: '',
            weight: '',
            mimetype: '',
            image: '',
            tasks:null
        }

        this.handleAdd = async() => {
            var date = new Date();
            var day = date.getDate();
            var month = date.getMonth()+1;
            var year = date.getFullYear();

            await this.repository.save( { image: this.state.image, 
                                            name: this.state.name, 
                                            description: this.state.description, 
                                            format: this.state.format,
                                            date: day + '.' + month + '.' + year,
                                            mimetype: this.state.mimetype,
                                            weight: this.state.weight,
                                            size: this.state.size,
                                            collection: this.state.collection}
                                             );
            this.setState( { tasks: null } );
            this.setState( { tasks: await this.repository.findAll() } );
            console.log(this.state.tasks);
          };
    }
    componentDidMount() {
       
        this.repository = new IndexedDbRepository();
        this.repository.findAll().then( tasks => this.setState( { tasks } ) );
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    handleImageChange(e) {

        /*let reader = new FileReader();
        let file = e.target.files[0];

        reader.onloadend = () => {
        this.setState({
            image: file,
            imagePreviewUrl: reader.result
        });
        }

        reader.readAsDataURL(file)*/
        var file = e.target.files[0];
        this.setState({
            mimetype: '.' + file.type.split('/')[1],
            weight: (file.size/1024/1024).toFixed(2) + " МБ"
        });

        console.log(file);
        var reader = new FileReader();
        console.log(reader);
        var preview = document.getElementById("preview");
        reader.readAsBinaryString(file);
        
				reader.onload = function(e) {
                    let bits = e.target.result;
                    preview.src = 'data:image/jpeg;base64,' + btoa(bits);
                    var img = new Image();
                    img.src = 'data:image/jpeg;base64,' + btoa(bits);
                    img.onload = () => {
                        
                    var size = img.height + "X" + img.width + " px";
                        this.setState({
                            image: bits,
                            size: size
                        });
                    }
                    
                }.bind(this);

    }



    render() {
        return (
            <div>
            <h1>Добавление</h1>
            <img width="300px" id="preview"/><br/>
            <input type="file" name="image" onChange={this.handleImageChange}/><br/>
            <input id="name" class="input_1" name="name" type="text" value={this.state.name} onChange={this.handleChange} required/><br/>
            <select name="format" onChange={this.handleChange}>
                <option value="Альбом">Альбом</option>
                <option value="Портрет">Портрет</option>
                <option value="Квадрат">Квадрат</option>
            </select><br/>
            <input id="description" class="input_1" name="description" type="text" value={this.state.description} onChange={this.handleChange} required/><br/>
            <input id="collection" class="input_1" name="collection" type="text" value={this.state.collection} onChange={this.handleChange} required/><br/>
            <button type="button" class="btn green" onClick={this.handleAdd}>Добавить</button>
                {/*<h1>BDFBFSBS</h1>
                <button type="button" onClick={this.add}>fbfdbds</button>
                <label>Заголовок</label><br/>
          <input id="name" class="input_1" name="name" type="text" value={this.state.name} onChange={this.handleChange} required/><br/>
          <label>Описание</label><br/>
          <input id="description" class="input_1" name="description" type="text" value={this.state.description} onChange={this.handleChange} required/><br/>
          <label>Текст</label><br/>
          <button type="button" class="btn green" onClick={this.handleAdd}>Добавить</button>
                <button type="button" class="btn green" onClick={() => this.handleDelete(2268)}>Удалить</button>*/}
            </div>
        );
    }
}


export default AddImg;
