import React from 'react';
import './css/CollectionsPage.css';
import IndexedDBRepository from './IndexedRepository.js';

class AddImg extends React.Component {
    constructor(props) {
        super(props);
        this.add = this.add.bind(this);
        this.state = {
            db: null,
            name: 'rgergev',
            format: 'ergerger',
            description: '', 
            image: ''
        }
    }
    componentDidMount() {
        if ( !window.indexedDB ) window.indexedDB = window.indexedDB || window.mozIndexedDB || window.webkitIndexedDB || window.msIndexedDB;
        if ( !window.IDBTransaction ) window.IDBTransaction = window.webkitIDBTransaction || window.msIDBTransaction;
        if ( !window.IDBKeyRange ) window.IDBKeyRange = window.webkitIDBKeyRange || window.msIDBKeyRange;
        var d;
        var openRequest = indexedDB.open("gallery",1);
        openRequest.onsuccess = function(e) {
            console.log("Success!");

            d = e.target.result;
            d.onerror = function(event){
                alert(event.target.errorcode);
            }
        }
 
        openRequest.onerror = function(e) {
            console.log("Error");
            console.dir(e);
        }

        alert(d);
        this.setState(
            {db: d}
        );
        
    }

    add() {
        alert("dfbdv");
        alert(this.state.db);
        var transaction = this.state.db.transaction(["notes"],"readwrite");
        var store = transaction.objectStore("notes");

        var request = store.add({name: this.state.name, format: this.state.format},1);
    }

    render() {
        return (
            <div>
                <h1>BDFBFSBS</h1>
                <button type="button" onClick={this.add}>fbfdbds</button>
            </div>
        );
    }
}


export default AddImg;
