import React from 'react';
import './css/CollectionsPage.css';
import IndexedDbRepository from './IndexedRepository';
import {Link, withRouter} from 'react-router-dom';

class AddCollectionPg extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            colName: '',
            colPreview: ''
        }

        this.handleAdd = async() => {

            await this.repository.save( 'collections', { colName: this.state.colName,
                                                colPreview: this.state.colPreview }
                                             );
            this.setState( { tasks: null } );
            this.setState( { tasks: await this.repository.findAll('photos') } );
            console.log(this.state.tasks);
          };
    }

    componentDidMount() {
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll('collections').then( collections => this.setState( { collections } ) );
    }

    handleImageChange(e) {
        var file = e.target.files[0];
        var reader = new FileReader();
        var preview = document.getElementById("colPreview");
        reader.readAsBinaryString(file);
        
				reader.onload = function(e) {
                    let bits = e.target.result;
                    preview.src = 'data:image/jpeg;base64,' + btoa(bits);    
                        this.setState({
                            image: bits
                        });    
                }.bind(this);

    }


    render() {
        return (
            <div>
            <h1>Добавление коллекции</h1>
            <img width="300px" id="colPreview"/><br/>
            <input type="file" name="image" onChange={this.handleImageChange}/><br/>
            <input id="name" class="input_1" name="name" type="text" value={this.state.name} onChange={this.handleChange} required/><br/>
            <button type="button" class="btn green" onClick={this.handleAdd}>Добавить</button>
            </div>
        );
    }
}

export default AddCollectionPg;
