import React from 'react';
import './css/CollectionsPage.css';
import './css/common.css';
import './css/Buttons.css';
import IndexedDbRepository from './IndexedRepository';
import {Link} from 'react-router-dom';
import Menu from './Menu';

class CollectionsPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            collections: []
        }

        
    }

    componentDidMount() {
        document.title = "Список коллекций";
        this.repository = new IndexedDbRepository("id");
        this.repository.findAll('collections').then( collections => this.setState( { collections } ) );
    }

    render() {
        return (
            <div class="container">
                <Menu />
                <div class = "header">
                    <h1>Коллекции</h1>
                </div>
                
                <div>
                    <ul class = "photosUl">
                    {() =>{ if(this.state.collections.length != 0)
                        {
                            alert(this.state.collections.length);
                            this.state.collections.map((collectiondata) => {
                                return (
                                    <li>
                                    <div class="photo">
                                    <Link to={/collectionpage/+ collectiondata.colName} class="btnlink" >
                                    <div class = "photoDiv">
                                    <img class = "photoImg" src = {'data:image/jpeg;base64,' + btoa(collectiondata.colPreview)}/>
                                    </div>
                                    <p class = "collectionName">{collectiondata.colName}</p></Link>
                                    
                                    </div>
                                    </li>
                                );
                        
                            });
                        }
                        else {
                            alert(this.state.collections.length);
                            return(
                                <p>Здесь пока ничего нет</p>
                            );
                        }
                    }}   
                    </ul>
                </div>
            </div>
        );
    }
}

export default CollectionsPage;
